define(function(require,exports,module){"use strict";var URL="https://api.jsdelivr.com/v1/{cdn}/libraries?fields=name,mainfile,versions";function downloadLibraries(cdn,callback){var req=new XMLHttpRequest;req.onload=callback;req.open("get",URL.replace("{cdn}",cdn));req.send()}function getLibraries(cdn,callback){function onDownloadComplete(){if(this.status!==200){callback("Error (Status "+this.status+"): Invalid library?");return}var libraries=JSON.parse(this.responseText);callback(null,libraries)}downloadLibraries(cdn,onDownloadComplete)}exports.getLibraries=getLibraries});